import { StructuredTool, BaseToolkit } from '@langchain/core/tools';
import { z } from 'zod';
import { CallbackManagerForToolRun } from '@langchain/core/callbacks/manager';
import { RunnableConfig } from '@langchain/core/runnables';

declare class MatonClient {
    private headers;
    constructor(apiKey: string);
    createConnection(app: string): Promise<any>;
    getConnection(connectionId: string): Promise<any>;
    listConnections(app?: string, status?: string): Promise<any>;
    deleteConnection(connectionId: string): Promise<any>;
    invokeAction(app: string, action: string, args: {
        [key: string]: any;
    }): Promise<any>;
    invokeAgent(app: string, userPrompt: string): Promise<any>;
}
declare class MatonAPI {
    maton: MatonClient;
    constructor(apiKey?: string);
    run(method: string, arg: any): Promise<any>;
}

declare class MatonTool extends StructuredTool {
    matonAPI: MatonAPI;
    method: string;
    name: string;
    description: string;
    schema: z.ZodObject<any, any, any, any>;
    constructor(MatonAPI: MatonAPI, method: string, description: string, schema: z.ZodObject<any, any, any, any, {
        [x: string]: any;
    }>);
    _call(arg: z.output<typeof this.schema>, _runManager?: CallbackManagerForToolRun, _parentConfig?: RunnableConfig): Promise<any>;
}

type Configuration = {
    apiKey?: string;
    app?: string;
    agent?: boolean;
    actions?: string[];
};

declare class MatonAgentToolkit implements BaseToolkit {
    private _maton;
    tools: MatonTool[];
    constructor(configuration: Configuration);
    getTools(): MatonTool[];
}

export { MatonAgentToolkit };
