var AGENT_ACTIONS = ["transfer-agent"];
var BUILTIN_ACTIONS = ["check-connection", "start-connection"];
function getMethod(app, action) {
  return `${app}_${action.replaceAll("-", "_")}`;
}
var checkConfiguration = (configuration) => {
  if (!configuration.app) {
    throw new Error("The app argument must be provided.");
  }
  if (configuration.agent && configuration.actions) {
    throw new Error("Both --agent and --actions arguments cannot be provided.");
  } else if (!configuration.agent && !configuration.actions) {
    throw new Error("Either --agent or --actions arguments must be provided.");
  }
  if (!ACCEPTED_APPS.includes(configuration.app)) {
    throw new Error(
      `Invalid app: ${configuration.app}. Accepted apps are: ${ACCEPTED_APPS.join(
        ", "
      )}`
    );
  }
  if (configuration.actions) {
    configuration.actions.forEach((action) => {
      if (action == "all") {
        return;
      }
      if (!ACCEPTED_ACTIONS[configuration.app].includes(action.trim())) {
        throw new Error(
          `Invalid action: ${action}. Accepted actions are: ${ACCEPTED_ACTIONS[configuration.app].join(", ")}`
        );
      }
    });
  }
};
var isToolAllowed = (tool, configuration) => {
  if (!configuration.app || !tool.method.startsWith(configuration.app)) {
    return false;
  }
  if (BUILTIN_ACTIONS.map(
    (action) => getMethod(configuration.app, action)
  ).includes(tool.method)) {
    return true;
  }
  if (configuration.agent) {
    return AGENT_ACTIONS.map(
      (action) => getMethod(configuration.app, action)
    ).includes(tool.method);
  } else if (configuration.actions) {
    if (configuration.actions.includes("all")) {
      return !AGENT_ACTIONS.map(
        (action) => getMethod(configuration.app, action)
      ).includes(tool.method);
    }
    return configuration.actions.map((action) => getMethod(configuration.app, action)).includes(tool.method);
  } else {
    return false;
  }
};

// src/shared/api.ts
var MatonClient = class {
  headers;
  constructor(apiKey) {
    this.headers = { "x-api-key": apiKey };
  }
  async createConnection(app) {
    const body = { app };
    const response = await fetch("https://api.maton.ai/create-connection", {
      method: "POST",
      headers: this.headers,
      body: JSON.stringify(body)
    });
    return response.json();
  }
  async getConnection(connectionId) {
    const body = { connection_id: connectionId };
    const response = await fetch("https://api.maton.ai/get-connection", {
      method: "POST",
      headers: this.headers,
      body: JSON.stringify(body)
    });
    return response.json();
  }
  async listConnections(app, status) {
    const body = { app, status };
    const response = await fetch("https://api.maton.ai/list-connections", {
      method: "POST",
      headers: this.headers,
      body: JSON.stringify(body)
    });
    return response.json();
  }
  async deleteConnection(connectionId) {
    const body = { connection_id: connectionId };
    const response = await fetch("https://api.maton.ai/delete-connection", {
      method: "POST",
      headers: this.headers,
      body: JSON.stringify(body)
    });
    return response.json();
  }
  async invokeAction(app, action, args) {
    const body = {
      app,
      action,
      args
    };
    const response = await fetch("https://api.maton.ai/invoke-action", {
      method: "POST",
      headers: this.headers,
      body: JSON.stringify(body)
    });
    return response.json();
  }
  async invokeAgent(app, userPrompt) {
    const body = {
      app,
      user_prompt: userPrompt
    };
    const response = await fetch("https://api.maton.ai/invoke-agent", {
      method: "POST",
      headers: this.headers,
      body: JSON.stringify(body)
    });
    return response.json();
  }
};
var MatonAPI = class {
  maton;
  constructor(apiKey) {
    const envApiKey = process.env.MATON_API_KEY ?? "";
    if (!apiKey && !envApiKey) {
      throw new Error(
        "Did not find MATON_API_KEY, please add an environment variable or pass it as a parameter"
      );
    }
    this.maton = new MatonClient(apiKey || envApiKey);
  }
  async run(method, arg) {
    const [app, ...rest] = method.split("_");
    const action = rest.join("-");
    let output = {};
    if (method.endsWith("check_connection")) {
      const listConnectionsResp = await this.maton.listConnections(
        app,
        "ACTIVE"
      );
      const connections = listConnectionsResp.connections;
      return connections && connections.length > 0;
    } else if (method.endsWith("start_connection")) {
      const createConnectionResp = await this.maton.createConnection(app);
      const getConnectionResp = await this.maton.getConnection(
        createConnectionResp.connection_id
      );
      const connection = getConnectionResp.connection;
      if (connection) {
        output = {
          connection_id: connection.connection_id,
          redirect_url: connection.url,
          instruction: "Ask user to open the redirect URL and complete the Oauth process. \n                    Once user completes the process and gets back, check again by calling check_connection"
        };
      }
    } else if (method.endsWith("transfer_agent")) {
      output = await this.maton.invokeAgent(app, arg.user_prompt);
    } else {
      output = await this.maton.invokeAction(app, action, arg);
    }
    return JSON.stringify(output);
  }
};
var api_default = MatonAPI;

